<body>
    <!-- style -->
    <style>
        :root {
            --accent: #0B8CE8;
            --black: #000;
            --white: #fff;

            --heading-1: 500 64px Inter;
            --button: 500 16px Inter;
            --body-1: 500 16px Inter;
            --body-2: 400 14px Inter;
        }

        body {
            font-family: Inter;

            .navbar {
                padding: 16px 100px;
                /* outline: 1px solid #E9ECF4; */

                .navbar-nav {
                    gap: 32px;

                    a {
                        font: var(--body-1);
                        color: var(--black);

                        &:hover {
                            color: var(--accent);
                        }
                    }

                }

                .button {
                    gap: 12px;

                    a {
                        font: var(--button);
                        text-decoration: none;
                        padding: 8px 16px;
                        border-radius: 6px;

                    }

                    .btn-login {
                        color: var(--accent);
                        border: 1px solid var(--accent);
                        transition: 0.1s all ease;

                        &:hover {
                            outline: 2px solid #4BAFF6;
                        }
                    }



                    .btn-reg {
                        background-color: var(--accent);
                        color: var(--white);
                        transition: 0.1s all ease;

                        &:hover {
                            outline: 2px solid #4BAFF6;
                        }
                    }
                }

                .profile {
                    align-items: center;
                    width: auto;
                    font: var(--button);
                    color: var(--black);

                    img {
                        width: 30px;
                    }
                }

            }
        }
    </style>

